import UIKit

protocol HomeDisplayLogic: class
{
  func displaySetIcon(viewModel: Home.SetIcon.ViewModel)
  func displayFetchEntries(viewModel: Home.FetchEntries.ViewModel)
}

class HomeViewController: UITableViewController, HomeDisplayLogic, EntryCellDelegate
{
  var interactor: HomeBusinessLogic?
  var router: (NSObjectProtocol & HomeRoutingLogic & HomeDataPassing)?

  // MARK: Object lifecycle
  
  override init(nibName nibNameOrNil: String?, bundle nibBundleOrNil: Bundle?)
  {
    super.init(nibName: nibNameOrNil, bundle: nibBundleOrNil)
    setup()
  }
  
  required init?(coder aDecoder: NSCoder)
  {
    super.init(coder: aDecoder)
    setup()
  }
  
  // MARK: Setup
  
  private func setup()
  {
    let viewController = self
    let interactor = HomeInteractor()
    let presenter = HomePresenter()
    let router = HomeRouter()
    viewController.interactor = interactor
    viewController.router = router
    interactor.presenter = presenter
    presenter.viewController = viewController
    router.viewController = viewController
    router.dataStore = interactor
  }
  
  // MARK: Routing
  
  override func prepare(for segue: UIStoryboardSegue, sender: Any?)
  {
    if let scene = segue.identifier {
      let selector = NSSelectorFromString("routeTo\(scene)WithSegue:")
      if let router = router, router.responds(to: selector) {
        router.perform(selector, with: segue)
      }
    }
  }
  
  // MARK: View lifecycle
  
  override func viewDidLoad()
  {
    super.viewDidLoad()
    fetchEntries()
  }
  
  // MARK: Table view
  
  override func numberOfSections(in tableView: UITableView) -> Int
  {
    return 1
  }
  
  override func tableView(_ tableView: UITableView, numberOfRowsInSection section: Int) -> Int
  {
    return displayedEntries.count
  }
  
  override func tableView(_ tableView: UITableView, cellForRowAt indexPath: IndexPath) -> UITableViewCell
  {
    let displayedEntry = displayedEntries[indexPath.row]
    var cell = tableView.dequeueReusableCell(withIdentifier: "EntryCell") as? EntryCell
    if cell == nil {
      cell = UITableViewCell(style: .default, reuseIdentifier: "EntryCell") as? EntryCell
    }
    cell!.displayedEntry = displayedEntry
    cell?.delegate = self
    return cell!
  }
  
  override func tableView(_ tableView: UITableView, didSelectRowAt indexPath: IndexPath)
  {
    // TODO: Route to detail view
  }
  
  // MARK: Fetch entries
  
  var displayedEntries = [Home.DisplayedEntry]()
  
  func fetchEntries()
  {
    let request = Home.FetchEntries.Request()
    interactor?.fetchEntries(request: request)
  }
  
  func displayFetchEntries(viewModel: Home.FetchEntries.ViewModel)
  {
    displayedEntries = viewModel.displayedEntries
    tableView.reloadData()
  }
  
  // MARK: Set icon
  
  private var indexPath: IndexPath?
  
  func entryCellButtonTapped(entryCell: EntryCell)
  {
    if let indexPath = tableView.indexPath(for: entryCell) {
      self.indexPath = indexPath
      takePhoto()
    }
  }
  
  func setIcon(icon: UIImage)
  {
    if let indexPath = indexPath {
      let request = Home.SetIcon.Request(icon: icon, indexPath: indexPath)
      interactor?.setIcon(request: request)
    }
  }
  
  func displaySetIcon(viewModel: Home.SetIcon.ViewModel)
  {
    displayedEntries[viewModel.indexPath.row] = viewModel.displayedEntry
    tableView.reloadRows(at: [viewModel.indexPath], with: .automatic)
  }
}

extension HomeViewController: UINavigationControllerDelegate, UIImagePickerControllerDelegate
{
  private func takePhoto()
  {
    let picker = UIImagePickerController()
    picker.sourceType = .photoLibrary
    picker.allowsEditing = true
    picker.delegate = self
    present(picker, animated: true)
  }
  
  func imagePickerController(_ picker: UIImagePickerController, didFinishPickingMediaWithInfo info: [String : Any])
  {
    picker.dismiss(animated: true)
    guard let image = info[UIImagePickerControllerEditedImage] as? UIImage else { return }
    setIcon(icon: image)
  }
}
